  .dropbtn {
  background-color: white;
  color: black;
  /* padding: 7px; */
  font-size: 16px;
  border: none;
  cursor: pointer;
}

.dropdown {
  position: relative;
  display: inline-block;
}

.dropdown-content {
  display: none;
  position: absolute;
  background-color: #f9f9f9;
  min-width: 160px;
  box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
  z-index: 1;
}

.dropdown-content a {
  color: black;
  padding: 12px 16px;
  text-decoration: none;
  display: block;
}

.dropdown-content a:hover {background-color: #f1f1f1}

.dropdown:hover .dropdown-content {
  display: block;
}

.dropdown:hover .dropbtn {
  background-color: #f1f1f1;
}

.my-navbar{
  box-shadow: 3px 7px 9px -6px rgba(0,0,0,0.33);
-webkit-box-shadow: 3px 7px 9px -6px rgba(0,0,0,0.33);
-moz-box-shadow: 3px 7px 9px -6px rgba(0,0,0,0.33);
}

