<style>
  .card-counter{
    box-shadow: 2px 2px 10px #DADADA;
    margin: 5px;
    padding: 20px 10px;
    background-color: #FF0000;
    height: 100px;
    border-radius: 5px;
    transition: .3s linear all;
  }

  .card-counter:hover{
    box-shadow: 4px 4px 20px #DADADA;
    transition: .3s linear all;
  }

  .card-counter.primary{
    background-color: #FF0000;
    color: #FFF;
  }

  .card-counter.danger{
    background-color: #FF0000;
    color: #FFF;
  }  

  .card-counter.success{
    background-color: #FF0000;
    color: #FFF;
  }  

  .card-counter.info{
    background-color: #FF0000;
    color: #FFF;
  }  

  .card-counter i{
    font-size: 5em;
    opacity: 0.7;
  }

  .card-counter .count-numbers{
    position: absolute;
    right: 35px;
    top: 20px;
    font-size: 32px;
    display: block;
  }

  .card-counter .count-name{
    position: absolute;
    right: 35px;
    top: 65px;
    font-style: italic;
    text-transform: capitalize;
    display: block;
    font-size: 18px;
  }
</style>
  
